    private void buansActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buansActionPerformed
      number2 = Double.parseDouble(buttEnt.getText()) ;
         
         double result = 0;
         
         if(operators=="+")
             result=number1+number2;
         else if(operators=="-")
             result=number1-number2;
         else if(operators=="*")
             result=number1*number2;
         else if(operators=="/")
             result=number1/number2;
         
         buttEnt.setText(result+"");
         
         operators=null;  
    }//GEN-LAST:event_buansActionPerformed

