    private void buCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buCActionPerformed
       String s = buttEnt.getText();
        
        buttEnt.setText(s.substring(0,s.length()-1));
    }//GEN-LAST:event_buCActionPerformed

